const l=()=>{document.addEventListener("DOMContentLoaded",()=>{const a=document.querySelector("#item-list"),t=document.querySelector("#load-more");let e=!1;async function o(){if(!e){e=!0,t.textContent="読込中…";try{const r=await(await fetch("https://jsonplaceholder.typicode.com/posts")).json();r.length>0?r.forEach(s=>{const c=document.createElement("li");c.textContent=s.title,a.appendChild(c)}):(t.textContent="これ以上データはありません",t.disabled=!0)}catch(n){console.error("Error fetching data:",n),t.content="エラーが発生しました"}finally{e=!1,t.textContent="もっと見る"}}}t.addEventListener("click",o),o()})};l();
